import React from "react";
import { Link } from "react-router-dom";
import "./styles/BadgesList.css";

class BadgesListItem extends React.Component {
	render() {
		return (
			<li key={this.props.badge.id} className="badgeList badgesContainer">
				<img
					className="BadgesListItem__avatar"
					src={this.props.badge.avatarUrl}
					alt={`${this.props.badge.firstName} ${this.props.badge.lastName}`}
				/>

				<div className="textBadges">
					<h3 className="nameBadges">
						{this.props.badge.firstName} {this.props.badge.lastName}
					</h3>
					<p className="infoBadges">
						<br />
						{this.props.badge.jobTitle}
					</p>
					<p className="socialBadges">@{this.props.badge.twitter}</p>
				</div>
			</li>
		);
	}
}

function BadgesList(props) {
	const badges = props.badges;

	const [query, setQuery] = React.useState("");

	const filterBadges = React.useMemo(() =>
		badges.filter((badge) => {
			return `${badge.firstName} ${badge.lastName}`
				.toLowerCase()
				.includes(query.toLowerCase);
		})
	);

	if (filterBadges.length === 0) {
		return (
			<div>
				<div className="form-group">
					<label>Filter Badges</label>
					<input
						type="text"
						className="form-control"
						value={query}
						onChange={(e) => {
							setQuery(e.target.value);
						}}
					/>
				</div>
				<h3>No badges were found</h3>
				<Link className="btn btn-primary" to="/Badges/new">
					Create new badge
				</Link>
			</div>
		);
	}

	return (
		<div className="BadgesList">
			<div className="form-group">
				<label>Filter Badges</label>
				<input
					type="text"
					className="form-control"
					value={query}
					onChange={(e) => {
						setQuery(e.target.value);
					}}
				/>
			</div>

			<ul className="list-unstyled">
				{filterBadges.map((badge) => {
					return (
						<li key={badge.id}>
							<Link
								className="text-reset text-decoration-none"
								to={`/badges/${badge.id}`}
							>
								<BadgesListItem badge={badge} />
							</Link>
						</li>
					);
				})}
			</ul>
		</div>
	);
}

export default BadgesList;
